import { Col } from "react-bootstrap";
export const ProjectCard = ({title, description, imgUrl }) => {
    return (
        <Col sm={12} md={6}>
            <div className="proj-imgbx">
                {
                    imgUrl === "../assets/img/load.svg" ? <img src={imgUrl} style={{animation: "rotate 2s linear infinite"}} alt="title"/> : <img src={imgUrl} alt="title"/>
                }
                <div className="proj-txtx">
                    <h4>{title}</h4>
                    <span>{description}</span>
                </div>
            </div>
        </Col>
    )
}
