        <Col sm={12} md={6}>
            <div className="proj-imgbx">
                {/*style=imgUrl === "../assets/img/load.svg"?{animation: "rotate 2s linear infinite"}:{}*/}
                
                 <img src={imgUrl} alt="title" style={{border: "2px solid #000000"}}/> 
                
